function changePorfolioScrollAll() {
  $("#post-blogs .ScrollChangePorfolio").click(function () {
    $("#portfolioContainer").removeClass("portfolioScrollAll");
    $("#post-blogs .portfolio > .item").css("height", "800px");

    //tung san pham
  });
  $("#post-blogs #filtrable li:first-child").click(function () {
    $("#post-blogs .portfolio > .item").css("height", "max-content");
    $("#portfolioContainer").addClass("portfolioScrollAll");
    $("#allProduct").click();

    // tat ca san pham
  });
}
changePorfolioScrollAll();
